function waitForApi(iframe) {
  return new Promise((resolve) => {
    let tries = 0;
    const maxTries = 200;
    const timer = setInterval(() => {
      tries += 1;
      const win = iframe.contentWindow;
      if (win && win.VOLLEY_API) {
        clearInterval(timer);
        resolve(win.VOLLEY_API);
        return;
      }
      if (tries > maxTries) {
        clearInterval(timer);
        resolve(null);
      }
    }, 50);
  });
}

